import { FormControl, InputLabel, MenuItem, NativeSelect, Select, Button } from '@material-ui/core'
import React, { Component } from 'react'
import { withRouter } from 'react-router-dom'
import Forum from './Forum'
import NavBar from './NavBar'


class StudentsList extends Component {
  constructor(props) {
    super(props)
    this.state = {
      classId: 0,
      goToForum: false
    }
  }



  render() {
    const { userData, students, teachers, classes, comments } = this.props
    const { classId, goToForum } = this.state
    const studentsAfterFilter = classId ? students.filter(s => s.classId == classId) : students
    return (
      <div style={{width:'100%'}}>
        <div>
          <Button onClick={() => this.setState({ goToForum: true })} >Fourum</Button>
                 &nbsp;&nbsp;&nbsp; | &nbsp;&nbsp;&nbsp;
                    <Button onClick={() => this.setState({ goToForum: false })}>StudentList</Button>
        </div>
        {goToForum ?
          <>
            <Forum
              userData={userData}
              comments={comments}
              addComments={this.props.setComments}
            />
          </>
          :
          <>
            <h1>Hello {userData.fName + " " + userData.lName}</h1>
            <h2>StudentsList</h2>
            <div>
              <FormControl id="outlined-basic" color="secondary" label="Enter ID" variant="outlined">
                <InputLabel id="demo-simple-select-outlined-label">Choose-Class</InputLabel>
                <Select
                  labelId="demo-simple-select-outlined-label"
                  id="demo-simple-select-outlined"
                  value={this.state.classId || 0}
                  onChange={(e) => this.setState({ classId: e.target.value })}
                  label="Age"
                >
                  < MenuItem value={0} > None</MenuItem>
                  {classes && classes.map(t => {
                    return < MenuItem value={t.classId} > {t.subject}</MenuItem>

                  })}
                </Select>
              </FormControl>
              {classId !== 0 && <p> Teacher Name :
                                {teachers.filter(t => t.classId == classId)[0].fName + " " + teachers.filter(t => t.classId == classId)[0].lName + " Class Id: " + teachers.filter(t => t.classId == classId)[0].classId} </p>}
            </div>

            <div>
              {studentsAfterFilter.map((student, index) => (
                <div style={{ width:'100%', maxWidth:'650px', position:'relative', border: '1px solid gray', borderRadius: '3px', textAlign: 'left', margin: 4, padding: 4, display: 'flex', justifyContent: 'center' }}>
                  {userData && userData.isStudent !== true && <button style={{ alignItems: 'center', color: 'red', border: '1px solid white', margin: 5 }}
                    onClick={() => this.props.deleteUser(student.userId)}>X</button>
                  }
                  <p style={{ fontWeight: 'bold', position: 'absolute',
                  top: '-22px',
                  left: '1px' }}
                  >{index + 1}</p>
                  <p>Name: {student.fName} {student.lName} - </p>
                  <p>
                    Class: {student.classId}
                  </p>
                </div>
              )
              )}
            </div>
          </>}
      </div>

    )
  }


}

export default withRouter(StudentsList);
